mapper.select:
select CATEGORYNO
from CATEGORY_TBL
where GROUPNO=#{groupNo}

        select nvl(sum(ACCOUNTBOOKSPEND),0)
from accountbook_tbl
where groupNo=#{groupNo} and accountbookRegDate between #{startDay} and #{lastDay}

        select nvl(sum(ACCOUNTBOOKINCOME),0)
from accountbook_tbl
where groupNo=#{groupNo} and accountbookRegDate between #{startDay} and #{lastDay}

select nvl(sum(accountbookIncome),0)-nvl(sum(accountbookSpend),0)
from accountbook_tbl
where groupNo=#{groupNo} and accountbookRegDate between #{startDay} and #{lastDay}

select accountbookNo,to_char(accountbookRegDate,'yyyy.MM.dd') accountbookRegdate,accountbookUsage,nvl(accountbookSpend,0)totalSpend,nvl(accountbookIncome,0)totalIncome,nvl(accountbookPersonnel,0) accountbookPersonnel,accountbookPlace
from accountbook_tbl join accountbook_tag_tbl using (accountbookNo)
where tagNo=#{tagNo}
order by accountbookRegDate,accountbookNo asc

select tagNo,tagName
from tag_tbl
where tagNo=#{tagNo}
